// We require the Hardhat Runtime Environment explicitly here. This is optional
// but useful for running the script in a standalone fashion through `node <script>`.
//
// When running the script with `npx hardhat run <script>` you'll find the Hardhat
// Runtime Environment's members available in the global scope.
const hre = require("hardhat");

async function main() {

  let provider = hre.ethers.provider;
  let signer = provider.getSigner();

  const myaddr = await signer.getAddress();

  console.log(myaddr);
  console.log(await signer.getBalance());

  // Hardhat always runs the compile task when running scripts with its command
  // line interface.
  //
  // If this script is run directly using `node` you may want to call compile
  // manually to make sure everything is compiled

  await hre.run('compile');

  const ETH = process.env.ETH;
  const USDC = process.env.USDC;
  const USDT = process.env.USDT;

  const swapContract = process.env.SWAP;
  let swap = await hre.ethers.getContractAt("Swap", swapContract, signer);

  // let _0x = "0xDef1C0ded9bec7F1a1670819833240f027b25EfF";
  // let _proxy = "0xDef1C0ded9bec7F1a1670819833240f027b25EfF";
  // let _name = "0x";

  // let registerTx = await swap.registerAdapter(
  //   _0x,
  //   _proxy,
  //   hre.ethers.utils.formatBytes32String("_name")
  // );
  // await registerTx.wait();
  // console.log("1inch router is registerred");

  // let index = await swap.getAdapterByIndex(1);
  // console.log(index);


  // return;

  let overrides = {
    value: ethers.utils.parseEther("0")
  };

  let data = "";
 
  let swapTx = await swap.swap(
    1,
    USDC,
    USDT,
    100000,
    data,
    overrides);
  await swapTx.wait()

  console.log("end");



}

// We recommend this pattern to be able to use async/await everywhere
// and properly handle errors.
main().catch((error) => {
  console.error(error);
  process.exitCode = 1;
});
